class FuzzingTool_Dialog_MinimizeFuzz_Child( FuzzingTool_Dialog_MinimizeFuzz ):
	def __init__( self, parent, program, fuzz):
		FuzzingTool.Dialog_MinimizeFuzz.__init__( self, parent )
