	def bSizer_YesorNoOnYesButtonClick( self, event ):
		# TODO: Implement bSizer_YesorNoOnYesButtonClick
		self.bSizer_YesorNoYes.Enable(False)
		self.bSizer_YesorNoNo.Enable(False)
